import React, { useEffect, useRef } from 'react';
import Matter from 'matter-js';

const MatterComponent = () => {
  const sceneRef = useRef(null);

  useEffect(() => {
    // Create engine and renderer
    const engine = Matter.Engine.create();
    const render = Matter.Render.create({
      element: sceneRef.current,
      engine: engine,
      options: {
        width: 250,
        height: 250,
        wireframes: false
      }
    });

    // Create bodies
    const ground = Matter.Bodies.rectangle(125, 240, 250, 20, { isStatic: true });
    const boxA = Matter.Bodies.rectangle(125, 50, 40, 40);
    const boxB = Matter.Bodies.rectangle(150, 20, 40, 40);

    Matter.World.add(engine.world, [boxA, boxB, ground]);

    // Add mouse control
    const mouse = Matter.Mouse.create(render.canvas);
    const mouseConstraint = Matter.MouseConstraint.create(engine, {
      mouse: mouse,
      constraint: {
        render: { visible: false }
      }
    });
    render.mouse = mouse;

    Matter.World.add(engine.world, mouseConstraint);

    // Add additional bodies
    const platform = Matter.Bodies.rectangle(200, 150, 150, 10, { isStatic: true });
    const stack = Matter.Composites.stack(180, 100, 2, 2, 0, 0, (x, y) => {
      return Matter.Bodies.polygon(x, y, 8, 15);
    });
    const ball = Matter.Bodies.circle(75, 200, 10);
    const sling = Matter.Constraint.create({
      pointA: { x: 75, y: 200 },
      bodyB: ball,
      stiffness: 0.05
    });

    Matter.World.add(engine.world, [platform, stack, ball, sling]);

    // Event listener for firing the ball
    let firing = false;
    Matter.Events.on(mouseConstraint, 'enddrag', (e) => {
      if (e.body === ball) firing = true;
    });

    // Run the engine and renderer
    Matter.Engine.run(engine);
    Matter.Render.run(render);

    // Cleanup on component unmount
    return () => {
      Matter.Render.stop(render);
      Matter.World.clear(engine.world);
      Matter.Engine.clear(engine);
      render.canvas.remove();
      render.textures = {};
    };
  }, []);

  return (
    <div ref={sceneRef} />
  );
};

export default MatterComponent;
